import React, {FC, useEffect, useMemo, useState} from "react";
import {Image as ImageAntd, Typography} from "antd";
import * as classNames from "classnames";
// own modules
import {truncateTheText} from "../../utils/truncateTheText.ts";
import {TValueOf} from "../../models/TUtils.ts";
import {IOriginalMessage} from "../../models/IStore/IRoom.ts";
// styles
import "./link-previewer.scss";

const {Text, Link} = Typography;

interface ILinkPreviewerProps {
    className?: string,
    data: TValueOf<Pick<IOriginalMessage, "firstLinkInfo">>
}

const checkImage = async (path: string) =>
    new Promise<HTMLImageElement>((resolve, reject) => {
        const img = new Image();
        img.onload = () => resolve(img);
        img.onerror = () => reject();

        img.src = path;
    });

const LinkPreviewer: FC<ILinkPreviewerProps> = ({data, className}) => {
    const [isUseRow, setIsUseRow] = useState<boolean>(false);
    const [hasLoadImage, setHasLoadImage] = useState<boolean>(false);

    useEffect(() => {
        if (!data) return;
        if (!data.image) {
            setHasLoadImage(true);
            return;
        }

        void checkImage(data.image)
            .then(image => {
                if (image.height < image.width) return;
                setIsUseRow(true);
            })
            .finally(() => {
                setHasLoadImage(true);
            });
    }, [data]);

    return useMemo(() => {
        if (!data || !hasLoadImage) return;

        return (
            <div className={classNames("link-previewer", isUseRow && "link-previewer_row", className)}>
                <div className="link-previewer__text-wrapper">
                    { data.shortTitle ?
                        (
                            <>
                                <Link strong href={data.url} title={data.shortTitle}>{data.shortTitle}</Link>
                                <Text strong>{data.title}</Text>
                            </>
                        )
                        :
                        <Link strong href={data.url} title={data.title}>{data.title}</Link>
                    }

                    <Text style={{lineHeight: "17px"}}>
                        {
                            truncateTheText({
                                text: data.description,
                                maxLength: 150
                            })
                        }
                    </Text>

                    {data.author && <Text>{data.author}</Text>}
                </div>

                <div className="link-previewer__image-wrapper">
                    <Link
                        href={data.url}
                        title={data.title}
                        className="link-previewer__image-link"
                    >
                        <ImageAntd
                            wrapperStyle={{width: "100%", height: "100%"}}
                            src={data.image}
                            alt={data.title + " preview"}
                            fallback="data:image/png;base64,"
                            preview={false}
                        />
                    </Link>
                </div>

            </div>
        );
    }, [className, data, hasLoadImage, isUseRow]);
};

export default LinkPreviewer;